          class="hidden tablet:block absolute right-0 -top-16 z-30 bg-[#F7F9F9] w-[484px] desktop:h-[180px]"
        >
          <div
            class="relative flex flex-col items-start justify-center h-[180px] ml-16"
          >
            <div class="flex items-center space-x-2">
              <svg
                width="43"
                :height="isLargeScreen ? 40 : 25"
                viewBox="0 0 43 40"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  d="M0 40L31 -1.35505e-06L43 -1.87959e-06L12 40L0 40Z"
                  fill="#39E8EA"
                />
              </svg>
              <h5 class="text-[12px] font-medium leading-snug mt-3">
                Explore <span class="text-[#39E8EA]">- </span>
              </h5>
            </div>
            <h3
              class="ml-[3.1rem] desktop:text-[35px] tablet:text-[30px] font-medium leading-snug"
            >
              Watch live here
            </h3>
          </div>
        </div>
      </div>
      <button v-if="playerActive" class="absolute z-40">
        <div
          @click.once="handleClick('playVideo', 'youtubeLazyVideo')"
          class="flex items-center space-x-4 group"
          @mouseover="buttonHovered()"
          @mouseleave="buttonUnhovered()"
        >
          <svg
            :class="[isHovered ? 'text-[#FFFFFF]' : 'text-[#39E8EA]']"
            width="60"
            height="61"
            viewBox="0 0 60 61"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <circle
              class="fill-stroke"
              cx="30.001"
              cy="30.002"
              r="29"
              fill="#39E8EA"
              :fill-opacity="fillOpacity"
              stroke="#39E8EA"
              stroke-width="2"
            />
            <path
              class="fill-current"
              d="M40 30L25 38.6603L25 21.3397L40 30Z"
              fill="#39E8EA"
            />
          </svg>
          <div class="relative z-30 text-[#FFFFFF] text-[26px]">PLAY VIDEO</div>
        </div>
      </button>
      <LazyYoutube
        ref="youtubeLazyVideo"
        :src="youtubeLink"
        iframe-class="mx-auto w-full h-full rounded z-0"
        max-width="100%"
        aspect-ratio="16:9"
        thumbnail-quality="maxres"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowfullscreen
      >
        <template #button>
          <div
            @mouseover="buttonHovered()"
            @mouseleave="buttonUnhovered()"
          ></div>
        </template>
      </LazyYoutube>
    </div>
  </div>
</template>

<script>
import { useMediaQuery } from "@vueuse/core";

export default {
  name: "YoutubePlayer",
  setup() {
    const isLargeScreen = useMediaQuery("(min-width: 1248px)");
    return {
      isLargeScreen
    };
  },
  data() {
    return {
      youtubeLink: "www.youtube.com/watch?v=aE8WrVYhLms",
      playVisible: true,
      playerActive: true,
      isHovered: false,
      fillOpacity: "0.15"
    };
  },
  methods: {
    handleClick(event, ref) {
      this.$refs[ref][event]();
      this.playVisible = !this.playVisible;
      this.playerActive = !this.playerActive;
    },
    buttonHovered() {
      this.fillOpacity = "1";
      this.isHovered = true;
    },
    buttonUnhovered() {
      this.fillOpacity = "0.15";
      this.isHovered = false;
    }
  }
};
</script>

<style>
.ly-button-wrapper {
  display: none;
}
</style>
